let add_click_action_to_like_button=function(){for(let t of $(".like-buttons")){let e=$(t);e.click((function(t){t.preventDefault(),$.ajax({type:"GET",url:e.attr("href")}).done((function(t){let n=e.attr("data-likes");console.log(n),n=t.data.deleted?parseInt(n)-1:parseInt(n)+1,e.attr("data-likes",n),e.find("span").html(n)})).fail((function(t){t&&console.log("error in completing the ajax request")}))}))}};add_click_action_to_like_button(),$("#friends-container .badge").click((function(t){t.preventDefault(),$("#user-chatbox").toggleClass("d-none")}));